-- Records of sobo_posts
-- ----------------------------
INSERT INTO `sobo_posts` VALUES (1, 'REJECT', 'ARTICLE', 1, 'Test', 'MARKDOWN', '# 一 一级标题\n\n## 1.1 二级标题\n\n- xxxx;\n- xxxx;\n\n## 1.2 二级标题\n\n1. xxxx;\n2. xxxx;    sfsfsfsfsfs', '<h1><a id=\"__0\"></a>一 一级标题</h1>\n<h2><a id=\"11__2\"></a>1.1 二级标题</h2>\n<ul>\n<li>xxxx;</li>\n<li>xxxx;</li>\n</ul>\n<h2><a id=\"12__7\"></a>1.2 二级标题</h2>\n<ol>\n<li>xxxx;</li>\n<li>xxxx;    sfsfsfsfsfs</li>\n</ol>\n', 21, 1, 3, 1, '', 0, 0, 1000, 0, 0, 0, '2023-03-09 19:27:58', '2023-03-24 02:28:08');
